using System;\nusing System.Xml;\nusing System.Xml.XPath;\n\npublic class Example\n{\n    public static void Main()\n    {\n        XmlDocument myXML = new XmlDocument();\n        myXML.LoadXml(@"<outerelement xmlns='http://example.com'><innerelement>Value</innerelement></outerelement>");\n\n        // Create an XmlNamespaceManager and add the default namespace.\n        XmlNamespaceManager nsmgr = new XmlNamespaceManager(myXML.NameTable);\n        nsmgr.AddNamespace("ns", "http://example.com");\n\n        // Create an XPathNavigator.\n        XPathNavigator navigator = myXML.CreateNavigator();\n\n        // Use the prefix in the XPath expression.\n        XPathNodeIterator result = navigator.Select("/ns:outerelement/ns:innerelement", nsmgr);\n\n        while (result.MoveNext())\n        {\n            Console.WriteLine(result.Current.OuterXml);\n        }\n    }\n}