using System;\nusing System.IO;\nusing Newtonsoft.Json;\n\nclass App2\n{\n    static void Main(string[] args)\n    {\n        if (args.Length != 1)\n        {\n            Console.WriteLine("Usage: App2 <path to temp file>");\n            return;\n        }\n\n        string tempFile = args[0];\n        if (!File.Exists(tempFile))\n        {\n            Console.WriteLine("File not found: " + tempFile);\n            return;\n        }\n\n        // Read JSON from the temporary file\n        string json = File.ReadAllText(tempFile);\n        object[] data = JsonConvert.DeserializeObject<object[]>(json);\n        \n        // Process the data\n        foreach (var item in data)\n        {\n            Console.WriteLine(item);\n        }\n\n        // Optionally delete the temporary file\n        File.Delete(tempFile);\n    }\n}