public IQueryable<Restaurant> All()\n{\n    return _context.Restaurants.Select(r => new Restaurant\n    {\n        Id = r.Id,\n        Name = r.Name\n        // Map other properties...\n    });\n}\n\npublic Restaurant GetById(Guid id)\n{\n    return _context.Restaurants\n        .Where(r => r.Id == id)\n        .Select(r => new Restaurant\n        {\n            Id = r.Id,\n            Name = r.Name\n            // Map other properties...\n        })\n        .SingleOrDefault();\n}\n\n// Usage\nvar result = Repository.GetById(new Guid("088ec7f4-63e8-4e3a-902f-fc6240df0a4b"));