using System;\nusing System.Net;\nusing System.Net.Mail;\n\nclass Program\n{\n    static void Main()\n    {\n        try\n        {\n            SmtpClient smtpClient = new SmtpClient("smtp.mail.yahoo.com")\n            {\n                Port = 587,\n                Credentials = new NetworkCredential("your_yahoo_email@yahoo.com", "your_password"),\n                EnableSsl = true,\n            };\n\n            MailMessage mail = new MailMessage\n            {\n                From = new MailAddress("your_yahoo_email@yahoo.com"),\n                Subject = "Test Mail",\n                Body = "This is a test mail from C#.NET",\n            };\n\n            mail.To.Add("recipient_email@example.com");\n\n            smtpClient.Send(mail);\n            Console.WriteLine("Mail Sent Successfully");\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Error: " + ex.Message);\n        }\n    }\n}