using System;\nusing System.Web.UI;\n\nnamespace YourNamespace\n{\n    public partial class Search : Page\n    {\n        protected void Page_Load(object sender, EventArgs e)\n        {\n            if (IsPostBack)\n            {\n                string searchType = HiddenFieldSearchType.Value;\n                UpdateSearchLabel(searchType);\n            }\n        }\n\n        private void UpdateSearchLabel(string searchType)\n        {\n            switch (searchType)\n            {\n                case "Web":\n                    LabelSearchText.Text = "Search the Web";\n                    break;\n                case "Images":\n                    LabelSearchText.Text = "Search for Images";\n                    break;\n                case "Video":\n                    LabelSearchText.Text = "Search for Videos";\n                    break;\n                case "More":\n                    LabelSearchText.Text = "Search More";\n                    break;\n                default:\n                    LabelSearchText.Text = "Search the Web";\n                    break;\n            }\n        }\n    }\n}