using System;\n\nclass Program\n{\n    static void Main()\n    {\n        // Create a DateTime object with Local time\n        DateTime localTime = new DateTime(2023, 10, 15, 14, 30, 0, DateTimeKind.Local);\n        Console.WriteLine("Local Time: " + localTime);\n\n        // Convert to UTC\n        DateTime utcTime = localTime.ToUniversalTime();\n        Console.WriteLine("UTC Time: " + utcTime);\n\n        // For an Unspecified DateTime, assume it is Local\n        DateTime unspecifiedTime = new DateTime(2023, 10, 15, 14, 30, 0, DateTimeKind.Unspecified);\n        DateTime unspecifiedToUtc = unspecifiedTime.ToUniversalTime();\n        Console.WriteLine("Unspecified to UTC: " + unspecifiedToUtc);\n\n        // For a UTC DateTime, it remains the same\n        DateTime utcDateTime = new DateTime(2023, 10, 15, 14, 30, 0, DateTimeKind.Utc);\n        DateTime utcToUtc = utcDateTime.ToUniversalTime();\n        Console.WriteLine("UTC to UTC: " + utcToUtc);\n    }\n}