using System;\nusing System.Drawing;\n\nclass Program\n{\n    static void Main()\n    {\n        // Specify the coordinates of the pixel\n        int x = 100; // X coordinate\n        int y = 150; // Y coordinate\n\n        // Create a bitmap to store the captured screen\n        using (Bitmap bmp = new Bitmap(1, 1))\n        {\n            // Create a graphics object from the bitmap\n            using (Graphics g = Graphics.FromImage(bmp))\n            {\n                // Capture the screen pixel at the specified coordinates\n                g.CopyFromScreen(x, y, 0, 0, new Size(1, 1));\n            }\n\n            // Get the color of the captured pixel\n            Color pixelColor = bmp.GetPixel(0, 0);\n\n            // Output the color of the pixel\n            Console.WriteLine("The color of the pixel at ({0}, {1}) is: {2}", x, y, pixelColor);\n        }\n    }\n}