using System;\n   using System.Data;\n   using Oracle.ManagedDataAccess.Client;\n   using System.IO;\n\n   class Program\n   {\n       static void Main()\n       {\n           string connectionString = "User Id=<your_user>;Password=<your_password>;Data Source=<your_data_source>";\n           string scriptFilePath = "path_to_your_sql_script.sql";\n\n           try\n           {\n               string script = File.ReadAllText(scriptFilePath);\n               ExecuteScript(script, connectionString);\n               Console.WriteLine("Script executed successfully.");\n           }\n           catch (Exception ex)\n           {\n               Console.WriteLine($"An error occurred: {ex.Message}");\n           }\n       }\n\n       static void ExecuteScript(string script, string connectionString)\n       {\n           using (OracleConnection connection = new OracleConnection(connectionString))\n           {\n               connection.Open();\n\n               // Split script into individual commands\n               string[] commands = script.Split(new string[] { ";" }, StringSplitOptions.RemoveEmptyEntries);\n\n               foreach (string commandText in commands)\n               {\n                   using (OracleCommand command = new OracleCommand(commandText, connection))\n                   {\n                       command.CommandType = CommandType.Text;\n                       command.ExecuteNonQuery();\n                   }\n               }\n           }\n       }\n   }