using System;\nusing System.Collections;\n\npublic class Report\n{\n    public string ReportCode { get; set; }\n    public string ReportName { get; set; }\n}\n\npublic class Program\n{\n    public static int FindReportIndex(ArrayList reportList, string reportToFind)\n    {\n        for (int i = 0; i < reportList.Count; i++)\n        {\n            Report report = (Report)reportList[i];\n            if (report.ReportCode == reportToFind)\n            {\n                return i;\n            }\n        }\n        return -1; // not found\n    }\n\n    public static void Main()\n    {\n        ArrayList ReportObjList = new ArrayList();\n        ReportObjList.Add(new Report { ReportCode = "5", ReportName = "Report 5" });\n        ReportObjList.Add(new Report { ReportCode = "6", ReportName = "Report 6" });\n        \n        string ReportToFind = "6";\n\n        int index = FindReportIndex(ReportObjList, ReportToFind);\n        if (index != -1)\n        {\n            Report foundReport = (Report)ReportObjList[index];\n            Console.WriteLine("Report found: " + foundReport.ReportName);\n        }\n        else\n        {\n            Console.WriteLine("Report not found");\n        }\n    }\n}