protected void Page_Load(object sender, EventArgs e)\n{\n    string currentPage = System.IO.Path.GetFileName(Request.Path).ToLower();\n\n    switch (currentPage)\n    {\n        case "screenshots.aspx":\n            screenshots.Attributes["class"] = "active";\n            break;\n        case "future.aspx":\n            future.Attributes["class"] = "active";\n            break;\n        case "news.aspx":\n            news.Attributes["class"] = "active";\n            break;\n        case "download.aspx":\n            download.Attributes["class"] = "active";\n            break;\n        case "index.aspx":\n            home.Attributes["class"] = "active";\n            break;\n    }\n}