using System;\nusing System.IO;\nusing System.Text.RegularExpressions;\nusing PdfiumViewer; // This is part of PDFium, a third-party library\n\nclass Program\n{\n    static void Main()\n    {\n        string pdfPath = "path/to/your/pdf/file.pdf";\n        string pdfText = ExtractTextFromPdf(pdfPath);\n\n        var hyperlinks = ExtractHyperlinks(pdfText);\n\n        foreach (var link in hyperlinks)\n        {\n            Console.WriteLine(link);\n        }\n    }\n\n    static string ExtractTextFromPdf(string path)\n    {\n        using (var document = PdfDocument.Load(path))\n        {\n            var text = string.Empty;\n            for (int i = 0; i < document.PageCount; i++)\n            {\n                text += document.GetPdfText(i);\n            }\n            return text;\n        }\n    }\n\n    static MatchCollection ExtractHyperlinks(string text)\n    {\n        string pattern = @"http[s]?://(?:[a-zA-Z]|[0-9]|[$-_@.&+]|[!*\\(\\),]|(?:%[0-9a-fA-F][0-9a-fA-F]))+";\n        Regex regex = new Regex(pattern, RegexOptions.Compiled | RegexOptions.IgnoreCase);\n        return regex.Matches(text);\n    }\n}