using System;\n   using System.IO;\n\n   class Program\n   {\n       static void Main()\n       {\n           string sambaSharePath = @"\\samba-server\share";\n           string fileName = "test.txt";\n           string filePath = Path.Combine(sambaSharePath, fileName);\n\n           try\n           {\n               // Write to the file\n               File.WriteAllText(filePath, "Hello, Samba!");\n\n               // Read from the file\n               string content = File.ReadAllText(filePath);\n               Console.WriteLine("File content: " + content);\n           }\n           catch (Exception ex)\n           {\n               Console.WriteLine("An error occurred: " + ex.Message);\n           }\n       }\n   }