// Program.cs\nusing System;\nusing System.AddIn.Hosting;\nusing System.Collections.ObjectModel;\n\nclass Program\n{\n    static void Main()\n    {\n        string addInRoot = @"Path\To\Your\AddIns";\n        AddInStore.Update(addInRoot);\n\n        Collection<AddInToken> addInTokens = AddInStore.FindAddIns(typeof(HostView), addInRoot);\n\n        foreach (var token in addInTokens)\n        {\n            HostView addIn = token.Activate<HostView>(AddInSecurityLevel.FullTrust);\n            Console.WriteLine(addIn.GetName());\n        }\n    }\n}