using System;\nusing System.Web.Profile;\nusing System.Web.Security;\n\npublic void AddProfileEntry(string username, Guid guidValue, string stringValue1, string stringValue2, int intValue)\n{\n    // Get the profile for the specified user\n    ProfileBase userProfile = ProfileBase.Create(username, true);\n\n    // Set the profile properties\n    userProfile.SetPropertyValue("GuidProperty", guidValue);\n    userProfile.SetPropertyValue("StringProperty1", stringValue1);\n    userProfile.SetPropertyValue("StringProperty2", stringValue2);\n    userProfile.SetPropertyValue("IntProperty", intValue);\n\n    // Save the profile\n    userProfile.Save();\n}\n\n// Example usage\npublic void AddProfilesToUsers()\n{\n    // Example users - replace with your actual user data\n    var users = new[]\n    {\n        new { Username = "user1", GuidValue = Guid.NewGuid(), StringValue1 = "Value1", StringValue2 = "Value2", IntValue = 1 },\n        new { Username = "user2", GuidValue = Guid.NewGuid(), StringValue1 = "Value3", StringValue2 = "Value4", IntValue = 2 }\n    };\n\n    foreach (var user in users)\n    {\n        AddProfileEntry(user.Username, user.GuidValue, user.StringValue1, user.StringValue2, user.IntValue);\n    }\n}