using System;\nusing System.IO;\nusing System.Net;\n\npublic class Program\n{\n    public static void Main()\n    {\n        string url = "http://example.com/file";\n        \n        HttpWebRequest request = (HttpWebRequest)WebRequest.Create(url);\n        request.Method = "HEAD"; // We only need the headers\n\n        try\n        {\n            using (HttpWebResponse response = (HttpWebResponse)request.GetResponse())\n            {\n                string contentType = response.ContentType;\n                Console.WriteLine("Content-Type: " + contentType);\n\n                if (contentType.Contains("text/html"))\n                {\n                    using (WebClient client = new WebClient())\n                    {\n                        byte[] data = client.DownloadData(url);\n                        string htmlContent = System.Text.Encoding.UTF8.GetString(data);\n                        Console.WriteLine("Downloaded HTML content: " + htmlContent);\n                    }\n                }\n                else\n                {\n                    Console.WriteLine("The content is not an HTML file. Skipping download.");\n                }\n            }\n        }\n        catch (WebException ex)\n        {\n            Console.WriteLine("Error: " + ex.Message);\n        }\n    }\n}