[AcceptVerbs(HttpVerbs.Post)]\npublic ActionResult GenericPostHandler(NewAccountInfo info)\n{\n    try\n    {\n        // Get the URL of the previous request\n        var referrerUrl = Request.UrlReferrer?.AbsolutePath;\n\n        // Here, you can determine the view name based on the referrer URL\n        // This is an example and may need to be adapted to your specific routing configuration\n        string currentView = GetViewNameFromUrl(referrerUrl);\n\n        Persist();\n\n        return RedirectToAction(StepManager.GetNextStep(currentView));\n    }\n    catch\n    {\n        return View();\n    }\n}\n\nprivate string GetViewNameFromUrl(string url)\n{\n    // Implement logic to extract the view name from the URL\n    // This example assumes a simple mapping, you may need to adjust accordingly\n    if (url.Contains("Index"))\n        return "Index";\n    else if (url.Contains("OtherView"))\n        return "OtherView";\n\n    // Add more conditions as necessary\n    return "DefaultView";\n}