// Reads NetworkStream into a byte buffer.\n\nNetworkStream ns;\nSystem.Net.Sockets.TcpClient client = new TcpClient();\n\n// Assuming the client is connected and NetworkStream is initialized properly\n\nbyte[] receiveBytes = new byte[client.ReceiveBufferSize];\nint bytesRead = ns.Read(receiveBytes, 0, receiveBytes.Length);\nString returndata = Encoding.UTF8.GetString(receiveBytes, 0, bytesRead);\n\n// Trim null characters\nreturndata = returndata.TrimEnd('\0');\n\n// Now concatenation should work as expected\nstring result = String.Concat(returndata, "test");\n// or\nstring result2 = returndata + "test";