using System;\nusing System.Runtime.InteropServices;\n\npublic class ParallelPort\n{\n    [DllImport("InpOut32.dll", EntryPoint = "Out32")]\n    private static extern void Output(int address, int value);\n\n    [DllImport("InpOut32.dll", EntryPoint = "Inp32")]\n    private static extern short Input(int address);\n\n    private const int DataPort = 0x378; // Common address for LPT1\n\n    public void WriteToPort(byte data)\n    {\n        Output(DataPort, data);\n    }\n\n    public byte ReadFromPort()\n    {\n        return (byte)Input(DataPort);\n    }\n}\n\nclass Program\n{\n    static void Main()\n    {\n        ParallelPort port = new ParallelPort();\n\n        // Write data to the parallel port\n        port.WriteToPort(0xFF);\n\n        // Read data from the parallel port\n        byte data = port.ReadFromPort();\n        Console.WriteLine($"Data read from port: {data:X2}");\n    }\n}