using System;\nusing System.Text.RegularExpressions;\n\npublic string SEO_makeTitle(object objTitle)\n{\n    if (objTitle == null)\n    {\n        return string.Empty;\n    }\n\n    string strTitle = Convert.ToString(objTitle);\n\n    if (string.IsNullOrWhiteSpace(strTitle))\n    {\n        return string.Empty;\n    }\n\n    // Replace special characters with space\n    strTitle = Regex.Replace(strTitle.Trim(), @"\W", " ", RegexOptions.Compiled);\n    // Replace multiple spaces with single space\n    strTitle = Regex.Replace(strTitle.Trim(), @"\s{2,}", " ", RegexOptions.Compiled);\n    // Replace spaces with hyphens and convert to lower case\n    strTitle = strTitle.Trim().Replace(" ", "-").ToLower();\n\n    return strTitle;\n}