public IEnumerable<YourObject> GetPagedData(int pageNumber, int pageSize)\n{\n    string query = @"SELECT * FROM (\n                        SELECT ROW_NUMBER() OVER (ORDER BY YourPrimaryKey) AS RowNum, *\n                        FROM YourTable\n                    ) AS Result\n                    WHERE RowNum >= @StartRow AND RowNum < @EndRow";\n\n    using (SqlConnection connection = new SqlConnection(connectionString))\n    {\n        SqlCommand command = new SqlCommand(query, connection);\n        command.Parameters.AddWithValue("@StartRow", (pageNumber - 1) * pageSize + 1);\n        command.Parameters.AddWithValue("@EndRow", pageNumber * pageSize + 1);\n\n        connection.Open();\n        using (SqlDataReader reader = command.ExecuteReader())\n        {\n            while (reader.Read())\n            {\n                yield return new YourObject\n                {\n                    // Map your data here\n                };\n            }\n        }\n    }\n}