using System;\nusing System.Data;\nusing System.Data.SqlClient;\nusing System.Linq;\n\npublic class VarBinaryDisplay\n{\n    public static void Main()\n    {\n        string connectionString = "your_connection_string";\n        string query = "SELECT YourVarBinaryColumn FROM YourTable";\n\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        using (SqlCommand command = new SqlCommand(query, connection))\n        {\n            connection.Open();\n            SqlDataAdapter adapter = new SqlDataAdapter(command);\n            DataTable dataTable = new DataTable();\n            adapter.Fill(dataTable);\n\n            // Convert varBinary data to hex string\n            foreach (DataRow row in dataTable.Rows)\n            {\n                byte[] varBinaryData = (byte[])row["YourVarBinaryColumn"];\n                row["YourVarBinaryColumn"] = BitConverter.ToString(varBinaryData).Replace("-", "");\n            }\n\n            // Bind dataTable to your GridView\n            YourGridView.DataSource = dataTable;\n            YourGridView.DataBind();\n        }\n    }\n}