using System;\n\npublic class Program\n{\n    public static void Main()\n    {\n        Guid guid1 = Guid.NewGuid();\n        Guid guid2 = Guid.NewGuid();\n\n        // Convert GUIDs to uppercase string representation for comparison\n        string guid1Str = guid1.ToString().ToUpper();\n        string guid2Str = guid2.ToString().ToUpper();\n\n        // Compare the standardized string representations\n        bool areEqual = guid1Str == guid2Str;\n\n        Console.WriteLine($"GUID 1: {guid1Str}");\n        Console.WriteLine($"GUID 2: {guid2Str}");\n        Console.WriteLine($"Are the GUIDs equal? {areEqual}");\n    }\n}