protected void Page_Load(object sender, EventArgs e)\n{\n    if (IsPostBack)\n    {\n        string youtubeEmbed = Request.Form["youtubeEmbed"];\n        \n        if (IsValidYouTubeEmbed(youtubeEmbed))\n        {\n            // Process the valid YouTube embed code\n            string videoId = ExtractVideoId(youtubeEmbed);\n        }\n        else\n        {\n            // Handle invalid input\n            Response.Write("Invalid YouTube embed code.");\n        }\n    }\n}\n\nprivate bool IsValidYouTubeEmbed(string embedCode)\n{\n    // Implement your validation logic here\n    // Example: Check if it matches a specific pattern\n    string pattern = @"<iframe.*?src=""https:\/\/www\.youtube\.com\/embed\/(.*?)"".*?<\/iframe>";\n    return Regex.IsMatch(embedCode, pattern, RegexOptions.IgnoreCase);\n}\n\nprivate string ExtractVideoId(string embedCode)\n{\n    // Extract the video ID from the embed code\n    Match match = Regex.Match(embedCode, @"<iframe.*?src=""https:\/\/www\.youtube\.com\/embed\/(.*?)"".*?<\/iframe>");\n    return match.Groups[1].Value;\n}