using System;\nusing System.Threading;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        RunHook runHook = new RunHook();\n\n        // Keep the application running indefinitely\n        while (true)\n        {\n            Thread.Sleep(1000); // Sleep for a short interval to avoid busy-waiting\n        }\n    }\n}\n\nclass RunHook\n{\n    private HookKeys hook;\n    public RunHook()\n    {\n        hook = new HookKeys();\n        hook.HookEvent += EventForHook;\n    }\n\n    private void EventForHook(HookEventArgs e, KeyBoardInfo keyBoardInfo, ref Boolean handled)\n    {\n        if ((keyBoardInfo.scanCode == 4) && (keyBoardInfo.vkCode == 114))\n            handled = true;\n    }\n}\n\n// Assuming HookKeys and KeyBoardInfo are defined elsewhere in your project