using System;\nusing System.Xml;\n\nclass Program\n{\n    static void Main()\n    {\n        XmlDocument xmlDoc = new XmlDocument();\n        xmlDoc.Load("example.xml");\n\n        Console.WriteLine(GenerateDtd(xmlDoc.DocumentElement));\n    }\n\n    static string GenerateDtd(XmlElement root)\n    {\n        var dtd = new System.Text.StringBuilder();\n        dtd.AppendLine($"<!ELEMENT {root.Name} (");\n\n        foreach (XmlNode child in root.ChildNodes)\n        {\n            if (child.NodeType == XmlNodeType.Element)\n            {\n                dtd.AppendLine($"{child.Name}, ");\n            }\n        }\n\n        dtd.AppendLine(")>");\n        \n        foreach (XmlNode child in root.ChildNodes)\n        {\n            if (child.NodeType == XmlNodeType.Element)\n            {\n                dtd.AppendLine($"<!ELEMENT {child.Name} (#PCDATA)>");\n            }\n        }\n\n        return dtd.ToString();\n    }\n}