using System;\nusing System.Linq;\nusing System.Windows.Forms;\n\npublic class TreeViewExample\n{\n    public void PopulateTreeView(TreeView treeView)\n    {\n        // Assuming you have a data context named 'DataContext' and entities 'Order' and 'Product'\n        using (var context = new DataContext())\n        {\n            var orders = from order in context.Orders\n                         select new\n                         {\n                             Order = order,\n                             Products = order.Products\n                         };\n\n            foreach (var orderItem in orders)\n            {\n                TreeNode orderNode = new TreeNode(orderItem.Order.OrderName); // Replace 'OrderName' with the appropriate property\n                \n                foreach (var product in orderItem.Products)\n                {\n                    TreeNode productNode = new TreeNode(product.ProductName); // Replace 'ProductName' with the appropriate property\n                    orderNode.Nodes.Add(productNode);\n                }\n\n                treeView.Nodes.Add(orderNode);\n            }\n        }\n    }\n}