using System;\nusing System.IO;\nusing System.Runtime.InteropServices;\nusing System.Threading;\n\npublic void UnmountAndDeletePst()\n{\n    _application.Session.RemoveStore(_personalFolder);\n    while (Marshal.ReleaseComObject(_personalFolder) >= 0) ;\n    _personalFolder = null;\n\n    // Retry mechanism to delete the file\n    string pstFilePath = "path_to_your_pst_file";\n    int maxRetries = 10;\n    int delayMilliseconds = 500;\n\n    for (int attempt = 0; attempt < maxRetries; attempt++)\n    {\n        try\n        {\n            if (File.Exists(pstFilePath))\n            {\n                File.Delete(pstFilePath);\n            }\n            break; // Exit the loop if the file is successfully deleted\n        }\n        catch (IOException)\n        {\n            // Wait for a while before retrying\n            Thread.Sleep(delayMilliseconds);\n        }\n    }\n}