using System;\nusing SourceSafeTypeLib;\n\nnamespace VSSIntegration\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            // Create a VSS database object and open the VSS database\n            VSSDatabase vssDatabase = new VSSDatabase();\n            vssDatabase.Open(@"\\path\to\srcsafe.ini", "username", "password");\n\n            // Get a project\n            VSSItem vssProject = vssDatabase.get_VSSItem("$/MyProject", false);\n\n            // Get the latest version of a file\n            VSSItem vssFile = vssProject.get_VSSItem("myfile.txt", false);\n            vssFile.Get(@"C:\path\to\local\directory", (int)VSSFlags.VSSFLAG_REPREPLACE);\n\n            // Determine who has the file checked out\n            VSSCheckouts checkouts = vssFile.Checkouts;\n            foreach (VSSCheckout checkout in checkouts)\n            {\n                Console.WriteLine($"File checked out by: {checkout.Username}");\n            }\n\n            // Undo another user's checkout\n            foreach (VSSCheckout checkout in checkouts)\n            {\n                if (checkout.Username.Equals("otheruser", StringComparison.OrdinalIgnoreCase))\n                {\n                    checkout.UndoCheckout();\n                    Console.WriteLine("Checkout undone for user: otheruser");\n                }\n            }\n\n            // Close the VSS database\n            vssDatabase.Close();\n        }\n    }\n}