// MainViewModel.cs\nusing System.ComponentModel.Composition;\nusing System.ComponentModel.Composition.Hosting;\n\npublic class MainViewModel\n{\n    [Import(typeof(IShapeViewModel))]\n    public IShapeViewModel ShapeViewModel { get; set; }\n\n    [Import("ShapeDataTemplate", typeof(DataTemplate))]\n    public DataTemplate ShapeDataTemplate { get; set; }\n\n    public MainViewModel()\n    {\n        Compose();\n    }\n\n    private void Compose()\n    {\n        var catalog = new AggregateCatalog();\n        catalog.Catalogs.Add(new AssemblyCatalog(typeof(MainViewModel).Assembly));\n        catalog.Catalogs.Add(new DirectoryCatalog("Extensions")); // Path to the directory containing MEF parts\n\n        var container = new CompositionContainer(catalog);\n        container.ComposeParts(this);\n    }\n}