using Xunit;\n\npublic class CalculatorTests\n{\n    [Fact]\n    public void Add_ReturnsCorrectSum()\n    {\n        // Arrange\n        var calculator = new Calculator();\n\n        // Act\n        var result = calculator.Add(2, 3);\n\n        // Assert\n        Assert.Equal(5, result);\n    }\n}\n\npublic class Calculator\n{\n    public int Add(int a, int b)\n    {\n        return a + b;\n    }\n}