using System;\nusing System.IO;\nusing System.Net;\nusing System.Xml;\n\npublic class XmlLoader\n{\n    public static XmlDocument LoadXmlThroughProxy(string url, string proxyAddress, string proxyUsername, string proxyPassword)\n    {\n        XmlDocument xmlDoc = new XmlDocument();\n\n        // Create a request to the URL\n        HttpWebRequest request = (HttpWebRequest)WebRequest.Create(url);\n        \n        // Set up the proxy\n        WebProxy proxy = new WebProxy(proxyAddress, true);\n        proxy.Credentials = new NetworkCredential(proxyUsername, proxyPassword);\n\n        request.Proxy = proxy;\n\n        // Get the response\n        using (HttpWebResponse response = (HttpWebResponse)request.GetResponse())\n        using (Stream responseStream = response.GetResponseStream())\n        {\n            // Load the XML document from the response stream\n            xmlDoc.Load(responseStream);\n        }\n\n        return xmlDoc;\n    }\n}