using System;\nusing System.Data.SqlClient;\nusing System.Web.Configuration;\n\npublic class DatabaseHelper\n{\n    public void GetUserIds()\n    {\n        string sql = "SELECT [UserId] FROM [UserProfiles] WHERE NOT [UserId] = @CurrentUserId";\n        string strCon = WebConfigurationManager.ConnectionStrings["SocialSiteConnectionString"].ConnectionString;\n\n        using (SqlConnection conn = new SqlConnection(strCon))\n        {\n            using (SqlCommand comm = new SqlCommand(sql, conn))\n            {\n                comm.Parameters.AddWithValue("@CurrentUserId", "CurrentUserId"); // Replace with actual current user id\n                conn.Open();\n                \n                using (SqlDataReader reader = comm.ExecuteReader())\n                {\n                    while (reader.Read())\n                    {\n                        int userId = reader.GetInt32(0); // Assuming UserId is an int\n                        // Process each userId as needed\n                        Console.WriteLine(userId);\n                    }\n                }\n            }\n        }\n    }\n}