using System;\nusing System.Net;\nusing System.Security.Cryptography.X509Certificates;\n\npublic class WebServiceClient\n{\n    public string CallWebService()\n    {\n        // Create web service instance\n        myWebService.TestWebService webservice = new myWebService.TestWebService();\n\n        // Set the URL to HTTPS\n        webservice.Url = "https://test.com/webservice?wsdl";\n\n        // Set proxy if needed\n        WebProxy proxy = new WebProxy("1.2.3.4", 8080);\n        proxy.Credentials = new NetworkCredential("username", "password");\n        webservice.Proxy = proxy;\n\n        // Set credentials for the web service\n        webservice.Credentials = new NetworkCredential("username", "password");\n\n        // Ignore SSL certificate validation (not recommended for production)\n        ServicePointManager.ServerCertificateValidationCallback = new RemoteCertificateValidationCallback(ValidateServerCertificate);\n\n        // Call the web method\n        string response = webservice.TestWebMethod();\n        return response;\n    }\n\n    // SSL certificate validation callback\n    public static bool ValidateServerCertificate(\n          object sender,\n          X509Certificate certificate,\n          X509Chain chain,\n          SslPolicyErrors sslPolicyErrors)\n    {\n        // Insecure: Always accept\n        return true;\n\n        // Secure: Validate certificate here\n        // return sslPolicyErrors == SslPolicyErrors.None;\n    }\n}