using System;\nusing System.Drawing;\nusing System.Collections.Generic;\n\npublic class FloodFill\n{\n    public static void FloodFillRegion(Bitmap bmp, Point pt, Color targetColor, Color replacementColor)\n    {\n        if (targetColor == replacementColor)\n            return;\n\n        Queue<Point> pixels = new Queue<Point>();\n        pixels.Enqueue(pt);\n\n        while (pixels.Count > 0)\n        {\n            Point a = pixels.Dequeue();\n            if (a.X < bmp.Width && a.X >= 0 &&\n                a.Y < bmp.Height && a.Y >= 0)\n            {\n                if (bmp.GetPixel(a.X, a.Y) == targetColor)\n                {\n                    bmp.SetPixel(a.X, a.Y, replacementColor);\n                    \n                    pixels.Enqueue(new Point(a.X - 1, a.Y));\n                    pixels.Enqueue(new Point(a.X + 1, a.Y));\n                    pixels.Enqueue(new Point(a.X, a.Y - 1));\n                    pixels.Enqueue(new Point(a.X, a.Y + 1));\n                }\n            }\n        }\n    }\n}\n\n// Usage example\npublic class Program\n{\n    public static void Main()\n    {\n        Bitmap bitmap = new Bitmap("path_to_your_image.png");\n        Point startPoint = new Point(50, 50); // Example starting point\n        Color targetColor = bitmap.GetPixel(startPoint.X, startPoint.Y);\n        Color fillColor = Color.Blue; // Example fill color\n\n        FloodFill.FloodFillRegion(bitmap, startPoint, targetColor, fillColor);\n\n        bitmap.Save("path_to_save_filled_image.png");\n    }\n}