using System.Windows;\nusing System.Windows.Media;\n\npublic class CustomDrawingVisual : DrawingVisual\n{\n    public CustomDrawingVisual()\n    {\n        using (DrawingContext dc = this.RenderOpen())\n        {\n            // Draw geometry, e.g., a line\n            Pen pen = new Pen(Brushes.Black, 2);\n            dc.DrawLine(pen, new Point(50, 50), new Point(200, 200));\n\n            // Draw text\n            FormattedText text = new FormattedText(\n                "Hello, WPF",\n                System.Globalization.CultureInfo.InvariantCulture,\n                FlowDirection.LeftToRight,\n                new Typeface("Arial"),\n                32,\n                Brushes.Black,\n                VisualTreeHelper.GetDpi(this).PixelsPerDip);\n\n            // Apply transformations\n            TransformGroup transformGroup = new TransformGroup();\n            transformGroup.Children.Add(new RotateTransform(45, 100, 100));\n            transformGroup.Children.Add(new TranslateTransform(100, 50));\n            dc.PushTransform(transformGroup);\n\n            // Draw the text\n            dc.DrawText(text, new Point(100, 100));\n\n            // Pop the transformation\n            dc.Pop();\n        }\n    }\n}\n\n// Then, in your main window or any other container\npublic partial class MainWindow : Window\n{\n    public MainWindow()\n    {\n        InitializeComponent();\n\n        CustomDrawingVisual customDrawingVisual = new CustomDrawingVisual();\n        this.Content = new VisualHost(customDrawingVisual);\n    }\n\n    private class VisualHost : FrameworkElement\n    {\n        private readonly Visual _visual;\n\n        public VisualHost(Visual visual)\n        {\n            _visual = visual;\n            this.AddVisualChild(_visual);\n        }\n\n        protected override int VisualChildrenCount => 1;\n\n        protected override Visual GetVisualChild(int index)\n        {\n            if (index != 0)\n                throw new ArgumentOutOfRangeException();\n            return _visual;\n        }\n    }\n}