public static class HtmlHelpers\n{\n    public static MvcHtmlString ActionButton(this HtmlHelper html, string text, string action, string controller, object routeValues = null, object htmlAttributes = null)\n    {\n        var urlHelper = new UrlHelper(html.ViewContext.RequestContext);\n        var url = urlHelper.Action(action, controller, routeValues);\n\n        var tagBuilder = new TagBuilder("button");\n        tagBuilder.MergeAttribute("type", "button");\n        tagBuilder.MergeAttribute("onclick", $"location.href='{url}'");\n        tagBuilder.InnerHtml = text;\n\n        var attributes = HtmlHelper.AnonymousObjectToHtmlAttributes(htmlAttributes);\n        tagBuilder.MergeAttributes(attributes);\n\n        return MvcHtmlString.Create(tagBuilder.ToString(TagRenderMode.Normal));\n    }\n}