public class ImageController : Controller\n   {\n       private readonly S3Service _s3Service;\n       private readonly string bucketName = "your-bucket-name";\n\n       public ImageController(S3Service s3Service)\n       {\n           _s3Service = s3Service;\n       }\n\n       [HttpPost]\n       public async Task<IActionResult> Upload(IFormFile file)\n       {\n           if (file == null || file.Length == 0)\n           {\n               return BadRequest("No file uploaded.");\n           }\n\n           using var stream = file.OpenReadStream();\n           var keyName = Guid.NewGuid().ToString() + Path.GetExtension(file.FileName);\n           var url = await _s3Service.UploadFileAsync(stream, bucketName, keyName);\n\n           return Ok(new { Url = url });\n       }\n\n       [HttpGet]\n       public async Task<IActionResult> Download(string keyName)\n       {\n           var stream = await _s3Service.GetFileAsync(bucketName, keyName);\n           return File(stream, "image/jpeg");\n       }\n   }