using System;\nusing System.Diagnostics;\n\npublic class TaskRunner\n{\n    public void RunTask(string someExe)\n    {\n        using (Process p = new Process())\n        {\n            p.StartInfo.UseShellExecute = false;\n            p.StartInfo.RedirectStandardOutput = true;\n            p.StartInfo.RedirectStandardError = true;\n            p.StartInfo.FileName = someExe;\n\n            try\n            {\n                p.Start();\n                string output = p.StandardOutput.ReadToEnd();\n                string error = p.StandardError.ReadToEnd();\n                p.WaitForExit();\n\n                if (p.ExitCode == 0)\n                {\n                    // Update tasks table with success status\n                    Console.WriteLine("Task completed successfully.");\n                    Console.WriteLine("Output: " + output);\n                }\n                else\n                {\n                    // Update tasks table with failure status\n                    Console.WriteLine("Task failed with exit code: " + p.ExitCode);\n                    Console.WriteLine("Error: " + error);\n                }\n            }\n            catch (Exception ex)\n            {\n                // Handle any exceptions that occur during process start\n                Console.WriteLine("Task failed to start. Exception: " + ex.Message);\n                // Update tasks table with exception information\n            }\n        }\n    }\n}