using System;\nusing System.Collections.Generic;\n\npublic class TimeZoneFinder\n{\n    public static List<TimeZoneInfo> FindTimeZonesByOffset(int timezoneOffset)\n    {\n        List<TimeZoneInfo> matchingTimeZones = new List<TimeZoneInfo>();\n        DateTime utcNow = DateTime.UtcNow;\n\n        foreach (var timeZone in TimeZoneInfo.GetSystemTimeZones())\n        {\n            // Get the current offset for the timezone\n            TimeSpan currentOffset = timeZone.GetUtcOffset(utcNow);\n\n            // Check if the current offset matches the provided offset\n            if (currentOffset.TotalMinutes == timezoneOffset)\n            {\n                matchingTimeZones.Add(timeZone);\n            }\n        }\n\n        return matchingTimeZones;\n    }\n}\n\nclass Program\n{\n    static void Main()\n    {\n        int offsetMinutes = -240; // Example offset in minutes\n        var timeZones = TimeZoneFinder.FindTimeZonesByOffset(offsetMinutes);\n\n        foreach (var timeZone in timeZones)\n        {\n            Console.WriteLine("Possible TimeZone: " + timeZone.DisplayName);\n        }\n    }\n}