using System;\nusing System.Web.Configuration;\nusing System.Web;\n\npublic class MimeMapHelper\n{\n    public static string GetMimeType(string fileExtension)\n    {\n        // Get the MimeMap section from the web server configuration\n        var mimeMapSection = (System.Web.Configuration.MimeMapSection)WebConfigurationManager.GetSection("system.webServer/staticContent");\n\n        if (mimeMapSection == null)\n        {\n            throw new InvalidOperationException("MimeMap section could not be found in the web server configuration.");\n        }\n\n        // Loop through the MimeMap collection and find the matching file extension\n        foreach (var mimeMap in mimeMapSection.MimeMaps)\n        {\n            if (mimeMap.FileExtension.Equals(fileExtension, StringComparison.OrdinalIgnoreCase))\n            {\n                return mimeMap.MimeType;\n            }\n        }\n\n        // Return a default mime type if none found\n        return "application/octet-stream";\n    }\n}