public class TokenManager\n   {\n       private static string token;\n       private static DateTime tokenExpiryTime;\n\n       public static string GetToken()\n       {\n           if (string.IsNullOrEmpty(token) || DateTime.UtcNow >= tokenExpiryTime)\n           {\n               RefreshToken();\n           }\n           return token;\n       }\n\n       private static void RefreshToken()\n       {\n           // Call authentication service to refresh token\n           token = GetNewTokenFromAuthService();\n           tokenExpiryTime = DateTime.UtcNow.AddMinutes(30); // Assuming token validity is 30 minutes\n       }\n\n       private static string GetNewTokenFromAuthService()\n       {\n           // Implement the logic to get a new token from your authentication service\n           return "new_token_value";\n       }\n   }