protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        DataSet ds = GetDataSet(); // Your method to get the DataSet\n        BindTreeView(ds);\n    }\n}\n\nprivate DataSet GetDataSet()\n{\n    DataSet ds = new DataSet();\n\n    // Adding ParentTable\n    DataTable parentTable = new DataTable("ParentTable");\n    parentTable.Columns.Add("ParentID", typeof(int));\n    parentTable.Columns.Add("ParentName", typeof(string));\n\n    // Adding ChildTable\n    DataTable childTable = new DataTable("ChildTable");\n    childTable.Columns.Add("ChildID", typeof(int));\n    childTable.Columns.Add("ParentID", typeof(int));\n    childTable.Columns.Add("ChildName", typeof(string));\n\n    // Adding sample data\n    parentTable.Rows.Add(1, "Parent 1");\n    parentTable.Rows.Add(2, "Parent 2");\n\n    childTable.Rows.Add(1, 1, "Child 1-1");\n    childTable.Rows.Add(2, 1, "Child 1-2");\n    childTable.Rows.Add(3, 2, "Child 2-1");\n\n    ds.Tables.Add(parentTable);\n    ds.Tables.Add(childTable);\n\n    // Adding relation\n    ds.Relations.Add("ParentChildRelation", parentTable.Columns["ParentID"], childTable.Columns["ParentID"]);\n\n    return ds;\n}\n\nprivate void BindTreeView(DataSet ds)\n{\n    TreeView1.Nodes.Clear();\n\n    foreach (DataRow parentRow in ds.Tables["ParentTable"].Rows)\n    {\n        TreeNode parentNode = new TreeNode(parentRow["ParentName"].ToString(), parentRow["ParentID"].ToString());\n        TreeView1.Nodes.Add(parentNode);\n\n        foreach (DataRow childRow in parentRow.GetChildRows("ParentChildRelation"))\n        {\n            TreeNode childNode = new TreeNode(childRow["ChildName"].ToString(), childRow["ChildID"].ToString());\n            parentNode.ChildNodes.Add(childNode);\n        }\n    }\n}