using System.Drawing;\nusing System.Windows.Forms;\n\npublic static SizeF GetAvgCharSizeF(Graphics g, Font font)\n{\n    string s = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz";\n    \n    Size textSize = TextRenderer.MeasureText(g, s, font, new Size(int.MaxValue, int.MaxValue), TextFormatFlags.NoPadding);\n    \n    float baseUnitX = (float)textSize.Width / s.Length;\n    float baseUnitY = (float)textSize.Height;\n    \n    return new SizeF(baseUnitX, baseUnitY);\n}