// ShapeView.xaml.cs\nusing System.ComponentModel.Composition;\nusing System.Windows;\n\n[Export("ShapeDataTemplate", typeof(DataTemplate))]\npublic partial class ShapeView : UserControl\n{\n    public ShapeView()\n    {\n        InitializeComponent();\n    }\n    \n    [Export("ShapeDataTemplate")]\n    public DataTemplate ShapeDataTemplate\n    {\n        get\n        {\n            string templateXaml = @"\n            <DataTemplate xmlns='http://schemas.microsoft.com/winfx/2006/xaml/presentation'\n                          xmlns:x='http://schemas.microsoft.com/winfx/2006/xaml'>\n                <local:ShapeView />\n            </DataTemplate>";\n            \n            return (DataTemplate)XamlReader.Parse(templateXaml);\n        }\n    }\n}