using Microsoft.Graph;\n\n// Method to get the Office Online edit link\nprivate async Task<string> GetOfficeOnlineEditLink(string fileId, string accessToken)\n{\n    GraphServiceClient graphClient = new GraphServiceClient(\n        new DelegateAuthenticationProvider(\n            (requestMessage) =>\n            {\n                requestMessage.Headers.Authorization = new AuthenticationHeaderValue("Bearer", accessToken);\n                return Task.CompletedTask;\n            }));\n\n    var item = await graphClient.Me.Drive.Items[fileId].CreateLink("edit", "organization").Request().PostAsync();\n    return item.Link.WebUrl;\n}\n\n// In your ASPX page\n<% foreach (var file in files) { %>\n    <a href="<%= GetOfficeOnlineEditLink(file.FileId, userAccessToken).Result %>">Edit <%= file.FileName %></a>\n<% } %>