using Microsoft.TeamFoundation.Client;\nusing Microsoft.TeamFoundation.VersionControl.Client;\nusing System;\n\nclass Program\n{\n    static void Main()\n    {\n        string tfsUri = "http://yourtfsserver:8080/tfs/YourCollection";\n        string labelName = "YourLabelName";\n        \n        TfsTeamProjectCollection tpc = new TfsTeamProjectCollection(new Uri(tfsUri));\n        VersionControlServer versionControl = tpc.GetService<VersionControlServer>();\n        \n        Label[] labels = versionControl.QueryLabels(labelName, null, null, true, null, VersionSpec.Latest);\n        \n        if (labels.Length == 0)\n        {\n            Console.WriteLine("Label not found.");\n            return;\n        }\n        \n        Label label = labels[0];\n        \n        foreach (var item in label.Items)\n        {\n            Console.WriteLine($"Item: {item.ServerItem}, Changeset: {item.ChangesetId}");\n        }\n    }\n}