using System;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        string pathToMonitor = @"E:\"; // Path to the flash drive\n        FileSystemWatcher watcher = new FileSystemWatcher\n        {\n            Path = pathToMonitor,\n            NotifyFilter = NotifyFilters.FileName | NotifyFilters.DirectoryName\n        };\n\n        watcher.Deleted += OnDeleted;\n        watcher.EnableRaisingEvents = true;\n\n        Console.WriteLine($"Monitoring {pathToMonitor} for deletions...");\n        Console.ReadLine();\n    }\n\n    private static void OnDeleted(object source, FileSystemEventArgs e)\n    {\n        Console.WriteLine($"File deleted: {e.FullPath}");\n        \n        // Logic to copy the deleted file to a "recycle bin" area on the flash drive\n        // This will require additional handling since the file is already deleted\n    }\n}