using System;\nusing System.Data;\nusing System.Data.SqlClient;\nusing System.IO;\n\npublic void RetrieveFile(int fileId, string destinationPath)\n{\n    string connectionString = System.Web.Configuration.WebConfigurationManager.ConnectionStrings["FAQ"].ToString();\n    \n    using (SqlConnection con = new SqlConnection(connectionString))\n    {\n        con.Open();\n        \n        // Create the SQL command to retrieve the file data\n        using (SqlCommand cmd = new SqlCommand("SELECT FileData FROM Files WHERE FileID = @fileId", con))\n        {\n            cmd.Parameters.Add(new SqlParameter("@fileId", SqlDbType.Int)).Value = fileId;\n            \n            // Execute the command and get the file data\n            byte[] fileData = (byte[])cmd.ExecuteScalar();\n            \n            // Ensure the destination directory exists\n            Directory.CreateDirectory(Path.GetDirectoryName(destinationPath));\n            \n            // Write the file data to the specified path\n            File.WriteAllBytes(destinationPath, fileData);\n        }\n    }\n}