using System;\nusing System.Data;\n\npublic class Program\n{\n    public static void Main()\n    {\n        // Creating a sample DataTable\n        DataTable dataTable = new DataTable();\n        dataTable.Columns.Add("Column1", typeof(string));\n\n        // Adding some sample data\n        dataTable.Rows.Add("A");\n        dataTable.Rows.Add("B");\n        dataTable.Rows.Add("C");\n        dataTable.Rows.Add("A");\n\n        // Adding the new column\n        dataTable.Columns.Add("NewColumn", typeof(string));\n\n        // Populating the new column based on the values in "Column1"\n        foreach (DataRow row in dataTable.Rows)\n        {\n            string column1Value = row["Column1"].ToString();\n            if (column1Value == "A")\n            {\n                row["NewColumn"] = "G";\n            }\n            else if (column1Value == "B")\n            {\n                row["NewColumn"] = "H";\n            }\n            else if (column1Value == "C")\n            {\n                row["NewColumn"] = "I";\n            }\n        }\n\n        // Displaying the results for verification\n        foreach (DataRow row in dataTable.Rows)\n        {\n            Console.WriteLine($"Column1: {row["Column1"]}, NewColumn: {row["NewColumn"]}");\n        }\n    }\n}