void RefreshFlashControl()\n{\n    // Dispose of the current control\n    if (axFlash != null)\n    {\n        axFlash.Stop();\n        axFlash.Dispose();\n        axFlash = null;\n    }\n\n    // Create a new instance of the control\n    axFlash = new AxShockwaveFlashObjects.AxShockwaveFlash();\n    axFlash.BeginInit();\n    this.Controls.Add(axFlash);\n    axFlash.EndInit();\n    axFlash.BringToFront();\n}\n\n// Call RefreshFlashControl() at appropriate intervals